package SET_1;

public class Addition_Two_Integers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 20 ;
		int b = 90 ;
		int c = a + b ;
		int d = b - a ;
		int e = a * b ;
		double f =(double)( b / a );
		float g = b % a ;      //Finding Reminder
		System.out.println("Addition :"+c) ;
		System.out.println("Substraction :"+d) ;
		System.out.println("Multi :"+e) ;
		System.out.println("Divide :"+f) ;
		System.out.println("Reminder :"+g) ;
		

	}

}
