package SET_1;

public class Swapping_integers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10 ;
		int b = 90 ;
		System.out.println("Before Swap : a =" +a+ " b= "+b) ;
		int temp = 0 ;
		temp = a ;
		a = b ;
		b =temp ;
		
		System.out.println("After Swap : a ="+a+ " b= " +b) ;

	}

}
